        const currentUrlWithoutQueryParams = window.location.origin + window.location.pathname;
        window.location.href = currentUrlWithoutQueryParams;
      }
    } catch (err) {
      context.grafana.notifyError(['Update', 'Failed to parse response.']);
    }
  });
} else {
  context.grafana.notifyError(['Update', 'An error occurred updating values.']);
}

